      [c, tikz={\draw [0 my edge] () [bend right] to (!l1.north) ;}
        [,phantom]
        [d
          [0]
          [1, name=my one]
        ]
      ]
      [,phantom]
    ]
  ]
\end{forest}
\begin{forest}
