\addtokomafont{caption}{\color{red!80!black}}
\addtokomafont{headsepline}{\color{red!80!black}}
\begin{document}
\chapter{lazy Leguan}
\section{walzing wombat}
\captionof{figure}{A figure caption}
\blindtext
\end{document}

