            &lt;c:out value="${row.pCount}" /&gt;
        &lt;/c:when&gt;
        &lt;c:otherwise&gt;
            &amp;nbsp;-&amp;nbsp;
        &lt;/c:otherwise&gt;
    &lt;/c:choose&gt;
&lt;/display:column&gt;

